import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Badge, Form, InputGroup, Dropdown } from 'react-bootstrap';
import apiClient from '../services/apiClient';

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searching, setSearching] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchSuggestions, setSearchSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  // Popular search keywords
  const popularKeywords = [
    'Java', 'JavaScript', 'React', 'Spring Boot', 'Python', 'AWS', 
    'Docker', 'Kubernetes', 'SQL', 'Machine Learning', 'DevOps',
    'Frontend', 'Backend', 'Full Stack', 'Web Development', 'Mobile'
  ];

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get('/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (keyword) => {
    try {
      setSearching(true);
      const response = await apiClient.get(`/courses/search?keyword=${encodeURIComponent(keyword)}`);
      setSearchResults(response.data);
      return response.data;
    } catch (error) {
      console.error('Error searching courses:', error);
      setSearchResults([]);
      return [];
    } finally {
      setSearching(false);
    }
  };

  const handleSearchChange = async (value) => {
    setSearchTerm(value);

    if (value.length >= 2) {
      const suggestions = await handleSearch(value);
      setSearchSuggestions(suggestions.slice(0, 5)); // Show top 5 suggestions
      setShowSuggestions(true);
    } else {
      setSearchSuggestions([]);
      setShowSuggestions(false);
      setSearchResults([]);
    }
  };

  const handleKeywordClick = async (keyword) => {
    setSearchTerm(keyword);
    setShowSuggestions(false);
    await handleSearch(keyword);
  };

  // Use search results if there's a search term, otherwise use all courses
  const displayCourses = searchTerm ? searchResults : courses;

  const filteredCourses = displayCourses.filter(course => {
    const matchesCategory = !selectedCategory || course.category === selectedCategory;
    return matchesCategory;
  });

  const getCategoryBadge = (category) => {
    const colors = {
      'TECHNICAL': 'primary',
      'SOFT_SKILLS': 'success',
      'COMPLIANCE': 'warning',
      'LEADERSHIP': 'info',
      'PRODUCTIVITY': 'secondary'
    };
    return <Badge bg={colors[category] || 'secondary'}>{category.replace('_', ' ')}</Badge>;
  };

  const getTypeBadge = (type) => {
    const colors = {
      'IN_PERSON': 'primary',
      'VIRTUAL': 'success',
      'HYBRID': 'warning',
      'SELF_PACED': 'info'
    };
    return <Badge bg={colors[type] || 'secondary'}>{type.replace('_', ' ')}</Badge>;
  };

  const getPlatformBadge = (materials) => {
    if (!materials) return null;

    if (materials.includes('udemy.com')) {
      return <Badge bg="purple">Udemy</Badge>;
    } else if (materials.includes('hackerrank.com')) {
      return <Badge bg="orange">HackerRank</Badge>;
    } else if (materials.includes('intershala.com')) {
      return <Badge bg="blue">Internshala</Badge>;
    } else if (materials.includes('coursera.org')) {
      return <Badge bg="teal">Coursera</Badge>;
    } else if (materials.includes('edx.org')) {
      return <Badge bg="dark">edX</Badge>;
    }
    return <Badge bg="secondary">Online</Badge>;
  };

  if (loading) {
    return (
      <Container>
        <div className="loading-container">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-book me-2"></i>
            Courses
          </h1>
          <p className="text-muted">Browse and enroll in training courses</p>
        </Col>
      </Row>

      {/* Search and Filter */}
      <Row className="mb-4">
        <Col md={6}>
          <div className="position-relative">
            <InputGroup>
              <InputGroup.Text>
                <i className="fas fa-search"></i>
              </InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="Search courses by title, description, or keywords..."
                value={searchTerm}
                onChange={(e) => handleSearchChange(e.target.value)}
                onFocus={() => setShowSuggestions(true)}
              />
              {searching && (
                <InputGroup.Text>
                  <div className="spinner-border spinner-border-sm text-primary" role="status">
                    <span className="visually-hidden">Searching...</span>
                  </div>
                </InputGroup.Text>
              )}
            </InputGroup>

            {/* Search Suggestions Dropdown */}
            {showSuggestions && (searchSuggestions.length > 0 || searchTerm.length >= 2) && (
              <div className="position-absolute w-100 bg-white border rounded shadow-sm" style={{ zIndex: 1000, top: '100%' }}>
                {searchSuggestions.map((course, index) => (
                  <div 
                    key={index} 
                    className="p-2 border-bottom cursor-pointer hover-bg-light"
                    onClick={() => {
                      setSearchTerm(course.title);
                      setShowSuggestions(false);
                      handleSearch(course.title);
                    }}
                    style={{ cursor: 'pointer' }}
                  >
                    <div className="fw-bold">{course.title}</div>
                    <div className="text-muted small">{course.category} • {course.type}</div>
                  </div>
                ))}
                {searchSuggestions.length === 0 && searchTerm.length >= 2 && (
                  <div className="p-2 text-muted">No courses found for "{searchTerm}"</div>
                )}
              </div>
            )}
          </div>
        </Col>
        <Col md={6}>
          <Form.Select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="">All Categories</option>
            <option value="TECHNICAL">Technical</option>
            <option value="SOFT_SKILLS">Soft Skills</option>
            <option value="COMPLIANCE">Compliance</option>
            <option value="LEADERSHIP">Leadership</option>
            <option value="PRODUCTIVITY">Productivity</option>
          </Form.Select>
        </Col>
      </Row>

      {/* Popular Keywords */}
      <Row className="mb-4">
        <Col>
          <div className="d-flex flex-wrap gap-2">
            <span className="text-muted me-2">Popular searches:</span>
            {popularKeywords.map((keyword, index) => (
              <Button
                key={index}
                variant="outline-primary"
                size="sm"
                onClick={() => handleKeywordClick(keyword)}
                className="mb-1"
              >
                {keyword}
              </Button>
            ))}
          </div>
        </Col>
      </Row>

      {/* Search Results Summary */}
      {searchTerm && (
        <Row className="mb-3">
          <Col>
            <div className="alert alert-info">
              <i className="fas fa-search me-2"></i>
              Found {searchResults.length} course(s) for "{searchTerm}"
              <Button 
                variant="outline-secondary" 
                size="sm" 
                className="ms-3"
                onClick={() => {
                  setSearchTerm('');
                  setSearchResults([]);
                  setShowSuggestions(false);
                }}
              >
                Clear Search
              </Button>
            </div>
          </Col>
        </Row>
      )}

      {/* Courses Grid */}
      <Row>
        {filteredCourses.length > 0 ? (
          filteredCourses.map((course) => (
            <Col md={6} lg={4} key={course.id} className="mb-4">
              <Card className="course-card h-100">
                <Card.Body>
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="card-title mb-0">{course.title}</h5>
                    <div>
                      {course.isMandatory && (
                        <Badge bg="danger" className="me-1">Mandatory</Badge>
                      )}
                      {getPlatformBadge(course.materials)}
                    </div>
                  </div>

                  <p className="card-text text-muted small mb-3">
                    {course.description}
                  </p>

                  <div className="mb-3">
                    {getCategoryBadge(course.category)}
                    <span className="mx-1">•</span>
                    {getTypeBadge(course.type)}
                  </div>

                  <div className="mb-3">
                    <small className="text-muted">
                      <i className="fas fa-clock me-1"></i>
                      {course.durationHours} hours
                    </small>
                    {course.trainer && (
                      <small className="text-muted ms-3">
                        <i className="fas fa-user me-1"></i>
                        {course.trainer.firstName} {course.trainer.lastName}
                      </small>
                    )}
                  </div>

                  {course.prerequisites && (
                    <div className="mb-3">
                      <small className="text-muted">
                        <strong>Prerequisites:</strong> {course.prerequisites}
                      </small>
                    </div>
                  )}

                  <div className="mt-auto">
                    <Button variant="primary" size="sm" className="me-2">
                      <i className="fas fa-eye me-1"></i>
                      View Details
                    </Button>
                    <Button variant="outline-success" size="sm">
                      <i className="fas fa-plus me-1"></i>
                      Enroll
                    </Button>
                    {course.materials && course.materials.startsWith('http') && (
                      <Button 
                        variant="outline-info" 
                        size="sm" 
                        className="ms-2"
                        onClick={() => window.open(course.materials, '_blank')}
                      >
                        <i className="fas fa-external-link-alt me-1"></i>
                        Go to Course
                      </Button>
                    )}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))
        ) : (
          <Col>
            <div className="empty-state">
              <i className="fas fa-search"></i>
              <p>No courses found matching your criteria.</p>
              <Button variant="outline-primary" onClick={() => {
                setSearchTerm('');
                setSelectedCategory('');
                setSearchResults([]);
              }}>
                Clear Filters
              </Button>
            </div>
          </Col>
        )}
      </Row>

      {/* Course Statistics */}
      <Row className="mt-5">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-chart-pie me-2"></i>
                Course Statistics
              </h5>
            </Card.Header>
            <Card.Body>
              <Row>
                <Col md={3} className="text-center">
                  <h4 className="text-primary">{courses.length}</h4>
                  <p className="text-muted mb-0">Total Courses</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-success">{courses.filter(c => c.isMandatory).length}</h4>
                  <p className="text-muted mb-0">Mandatory Courses</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-info">{courses.filter(c => c.category === 'TECHNICAL').length}</h4>
                  <p className="text-muted mb-0">Technical Courses</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-warning">{courses.filter(c => c.type === 'VIRTUAL').length}</h4>
                  <p className="text-muted mb-0">Virtual Courses</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Courses;
