import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Modal, Form, Alert } from 'react-bootstrap';
import { EnrollmentService } from '../services/EnrollmentService';
import { CourseService } from '../services/CourseService';

const Enrollments = ({ user }) => {
  const [enrollments, setEnrollments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showEnrollModal, setShowEnrollModal] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [enrollmentType, setEnrollmentType] = useState('OPTIONAL');

  useEffect(() => {
    loadEnrollments();
    loadCourses();
  }, []);

  const loadEnrollments = async () => {
    try {
      setLoading(true);
      const data = await EnrollmentService.getEnrollmentsByUser(user.id);
      setEnrollments(data);
    } catch (err) {
      setError('Failed to load enrollments');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadCourses = async () => {
    try {
      const data = await CourseService.getAllCourses();
      setCourses(data);
    } catch (err) {
      console.error('Failed to load courses:', err);
    }
  };

  const handleEnroll = async () => {
    try {
      const enrollmentData = {
        userId: user.id,
        courseId: parseInt(selectedCourse),
        type: enrollmentType
      };

      await EnrollmentService.createEnrollment(enrollmentData);
      setShowEnrollModal(false);
      setSelectedCourse('');
      setEnrollmentType('OPTIONAL');
      loadEnrollments(); // Reload enrollments
    } catch (err) {
      setError('Failed to enroll in course');
      console.error(err);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      'ENROLLED': 'primary',
      'IN_PROGRESS': 'warning',
      'COMPLETED': 'success',
      'DROPPED': 'danger',
      'FAILED': 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      'MANDATORY': 'danger',
      'OPTIONAL': 'info',
      'ASSIGNED': 'warning'
    };
    return <Badge bg={variants[type] || 'secondary'}>{type}</Badge>;
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-user-graduate me-2"></i>
            My Enrollments
          </h1>
          <p className="text-muted">Manage your course enrollments and track progress</p>
        </Col>
        <Col xs="auto">
          <Button 
            variant="primary" 
            onClick={() => setShowEnrollModal(true)}
            className="d-flex align-items-center"
          >
            <i className="fas fa-plus me-2"></i>
            Enroll in Course
          </Button>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Enrollment History
              </h5>
            </Card.Header>
            <Card.Body>
              {enrollments.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-user-graduate fa-3x text-muted mb-3"></i>
                  <h5>No Enrollments Found</h5>
                  <p className="text-muted">You haven't enrolled in any courses yet.</p>
                  <Button variant="primary" onClick={() => setShowEnrollModal(true)}>
                    Enroll in Your First Course
                  </Button>
                </div>
              ) : (
                <Table responsive striped>
                  <thead>
                    <tr>
                      <th>Course</th>
                      <th>Type</th>
                      <th>Status</th>
                      <th>Enrolled Date</th>
                      <th>Completion Date</th>
                      <th>Grade</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {enrollments.map((enrollment) => (
                      <tr key={enrollment.id}>
                        <td>
                          <strong>{enrollment.course?.title}</strong>
                          <br />
                          <small className="text-muted">{enrollment.course?.category}</small>
                        </td>
                        <td>{getTypeBadge(enrollment.type)}</td>
                        <td>{getStatusBadge(enrollment.status)}</td>
                        <td>
                          {enrollment.enrolledAt ? 
                            new Date(enrollment.enrolledAt).toLocaleDateString() : 
                            'N/A'
                          }
                        </td>
                        <td>
                          {enrollment.completedAt ? 
                            new Date(enrollment.completedAt).toLocaleDateString() : 
                            'N/A'
                          }
                        </td>
                        <td>
                          {enrollment.grade || 'N/A'}
                        </td>
                        <td>
                          <Button 
                            size="sm" 
                            variant="outline-primary" 
                            className="me-1"
                          >
                            <i className="fas fa-eye"></i>
                          </Button>
                          {enrollment.status === 'ENROLLED' && (
                            <Button 
                              size="sm" 
                              variant="outline-success"
                              className="me-1"
                            >
                              <i className="fas fa-play"></i>
                            </Button>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Enroll Modal */}
      <Modal show={showEnrollModal} onHide={() => setShowEnrollModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Enroll in Course</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Select Course</Form.Label>
              <Form.Select 
                value={selectedCourse} 
                onChange={(e) => setSelectedCourse(e.target.value)}
              >
                <option value="">Choose a course...</option>
                {courses
                  .filter(course => !enrollments.some(enrollment => enrollment.course?.id === course.id))
                  .map(course => (
                    <option key={course.id} value={course.id}>
                      {course.title} - {course.category}
                    </option>
                  ))
                }
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Enrollment Type</Form.Label>
              <Form.Select 
                value={enrollmentType} 
                onChange={(e) => setEnrollmentType(e.target.value)}
              >
                <option value="OPTIONAL">Optional</option>
                <option value="ASSIGNED">Assigned</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEnrollModal(false)}>
            Cancel
          </Button>
          <Button 
            variant="primary" 
            onClick={handleEnroll}
            disabled={!selectedCourse}
          >
            Enroll
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Enrollments;
