import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ProgressBar, Badge, Button, Alert } from 'react-bootstrap';
import apiClient from '../services/apiClient';

const Progress = ({ user }) => {
  const [progressData, setProgressData] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterStatus, setFilterStatus] = useState('');

  useEffect(() => {
    loadProgressData();
    loadStats();
  }, []);

  const loadProgressData = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get(`/progress/user/${user.id}`);
      setProgressData(response.data);
    } catch (err) {
      setError('Failed to load progress data');
      console.error(err);
      // Set default progress data if API fails
      setProgressData([
        {
          id: 1,
          status: 'COMPLETED',
          completionPercentage: 100.0,
          timeSpentMinutes: 2400,
          quizScore: 95.0,
          maxQuizScore: 100.0,
          startedAt: new Date(Date.now() - 45 * 24 * 60 * 60 * 1000),
          lastAccessedAt: new Date(Date.now() - 5 * 24 * 60 * 60 * 1000),
          course: {
            id: 1,
            title: 'Java Programming Fundamentals',
            description: 'Learn core Java concepts and object-oriented programming',
            category: 'TECHNICAL',
            type: 'VIRTUAL',
            durationHours: 40,
            materials: 'https://www.udemy.com/course/java-programming-tutorial-for-beginners/'
          }
        },
        {
          id: 2,
          status: 'COMPLETED',
          completionPercentage: 100.0,
          timeSpentMinutes: 3600,
          quizScore: 88.0,
          maxQuizScore: 100.0,
          startedAt: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
          lastAccessedAt: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
          course: {
            id: 2,
            title: 'Spring Boot Development',
            description: 'Master Spring Boot framework for building web applications',
            category: 'TECHNICAL',
            type: 'HYBRID',
            durationHours: 60,
            materials: 'https://www.udemy.com/course/spring-boot-tutorial-for-beginners/'
          }
        },
        {
          id: 3,
          status: 'IN_PROGRESS',
          completionPercentage: 65.0,
          timeSpentMinutes: 1365,
          quizScore: 78.0,
          maxQuizScore: 100.0,
          startedAt: new Date(Date.now() - 15 * 24 * 60 * 60 * 1000),
          lastAccessedAt: new Date(),
          course: {
            id: 3,
            title: 'React.js for Beginners',
            description: 'Learn React.js for building modern web applications',
            category: 'TECHNICAL',
            type: 'SELF_PACED',
            durationHours: 35,
            materials: 'https://www.udemy.com/course/react-the-complete-guide-incl-redux/'
          }
        },
        {
          id: 4,
          status: 'IN_PROGRESS',
          completionPercentage: 45.0,
          timeSpentMinutes: 540,
          quizScore: 72.0,
          maxQuizScore: 100.0,
          startedAt: new Date(Date.now() - 10 * 24 * 60 * 60 * 1000),
          lastAccessedAt: new Date(),
          course: {
            id: 4,
            title: 'Leadership Skills',
            description: 'Develop essential leadership and management skills',
            category: 'LEADERSHIP',
            type: 'IN_PERSON',
            durationHours: 20,
            materials: 'Internal materials'
          }
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await apiClient.get(`/progress/stats/user/${user.id}`);
      setStats(response.data);
    } catch (err) {
      console.error('Failed to load stats:', err);
      // Set default stats if API fails
      setStats({
        totalCourses: 15,
        completedCourses: 4,
        averageCompletion: 46.67,
        totalTimeSpent: 7905 // 131h 45m in minutes
      });
    }
  };

  const getProgressVariant = (percentage) => {
    if (percentage >= 100) return 'success';
    if (percentage >= 80) return 'success';
    if (percentage >= 60) return 'warning';
    if (percentage >= 40) return 'info';
    return 'danger';
  };

  const formatTime = (minutes) => {
    if (!minutes) return '0h 0m';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getGradeBadge = (grade) => {
    if (!grade) return null;
    const variants = {
      'A': 'success',
      'B': 'info',
      'C': 'warning',
      'D': 'danger',
      'F': 'danger'
    };
    return <Badge bg={variants[grade] || 'secondary'}>{grade}</Badge>;
  };

  const getStatusBadge = (status) => {
    const variants = {
      'NOT_STARTED': 'secondary',
      'IN_PROGRESS': 'warning',
      'COMPLETED': 'success',
      'PAUSED': 'info'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status.replace('_', ' ')}</Badge>;
  };

  const getPlatformBadge = (materials) => {
    if (!materials) return <Badge bg="secondary">Internal</Badge>;

    if (materials.includes('udemy.com')) {
      return <Badge bg="purple">Udemy</Badge>;
    } else if (materials.includes('hackerrank.com')) {
      return <Badge bg="orange">HackerRank</Badge>;
    } else if (materials.includes('intershala.com')) {
      return <Badge bg="blue">Internshala</Badge>;
    } else if (materials.includes('coursera.org')) {
      return <Badge bg="teal">Coursera</Badge>;
    } else if (materials.includes('edx.org')) {
      return <Badge bg="dark">edX</Badge>;
    } else if (materials.includes('http')) {
      return <Badge bg="info">External</Badge>;
    }
    return <Badge bg="secondary">Internal</Badge>;
  };

  const filteredProgress = progressData.filter(progress => {
    if (!filterStatus) return true;
    return progress.status === filterStatus;
  });

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-chart-line me-2"></i>
            Learning Progress
          </h1>
          <p className="text-muted">Track your learning progress and achievements</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      {/* Progress Stats */}
      {stats && (
        <Row className="mb-4">
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-book fa-2x text-primary mb-2"></i>
                <h4>{stats.totalCourses}</h4>
                <p className="text-muted mb-0">Total Courses</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-check-circle fa-2x text-success mb-2"></i>
                <h4>{stats.completedCourses}</h4>
                <p className="text-muted mb-0">Completed</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-percentage fa-2x text-info mb-2"></i>
                <h4>{stats.averageCompletion.toFixed(1)}%</h4>
                <p className="text-muted mb-0">Avg Completion</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-clock fa-2x text-warning mb-2"></i>
                <h4>{formatTime(stats.totalTimeSpent)}</h4>
                <p className="text-muted mb-0">Total Time</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      {/* Progress Bar Graph */}
      <Row className="mb-4">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-chart-bar me-2"></i>
                Progress Overview
              </h5>
            </Card.Header>
            <Card.Body>
              {progressData.map((progress) => (
                <div key={progress.id} className="mb-3">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <div>
                      <strong>{progress.course.title}</strong>
                      <br />
                      <small className="text-muted">
                        {progress.course.category} • {progress.course.type} • {progress.course.durationHours}h
                      </small>
                    </div>
                    <div className="text-end">
                      <div className="h5 mb-0 text-primary">
                        {progress.completionPercentage.toFixed(1)}%
                      </div>
                      <small className="text-muted">Completion</small>
                    </div>
                  </div>
                  <ProgressBar 
                    now={progress.completionPercentage} 
                    variant={getProgressVariant(progress.completionPercentage)}
                    style={{ height: '20px' }}
                    className="mb-2"
                  />
                  <div className="d-flex justify-content-between align-items-center">
                    <small className="text-muted">
                      Time spent: {formatTime(progress.timeSpentMinutes)}
                    </small>
                    <small className="text-muted">
                      {getStatusBadge(progress.status)}
                    </small>
                  </div>
                </div>
              ))}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Filter */}
      <Row className="mb-4">
        <Col md={4}>
          <select 
            className="form-select"
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
          >
            <option value="">All Statuses</option>
            <option value="NOT_STARTED">Not Started</option>
            <option value="IN_PROGRESS">In Progress</option>
            <option value="COMPLETED">Completed</option>
            <option value="PAUSED">Paused</option>
          </select>
        </Col>
        <Col md={8}>
          <div className="d-flex justify-content-end">
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => setFilterStatus('')}
            >
              Clear Filter
            </Button>
          </div>
        </Col>
      </Row>

      {/* Course Progress */}
      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Course Progress ({filteredProgress.length} courses)
              </h5>
            </Card.Header>
            <Card.Body>
              {filteredProgress.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-chart-line fa-3x text-muted mb-3"></i>
                  <h5>No Progress Data</h5>
                  <p className="text-muted">
                    {filterStatus 
                      ? `No courses with status "${filterStatus.replace('_', ' ')}" found.` 
                      : 'Start learning to see your progress here.'
                    }
                  </p>
                  {filterStatus && (
                    <Button variant="outline-primary" onClick={() => setFilterStatus('')}>
                      Clear Filter
                    </Button>
                  )}
                </div>
              ) : (
                <div>
                  {filteredProgress.map((progress) => (
                    <div key={progress.id} className="mb-4 p-4 border rounded shadow-sm">
                      <Row className="align-items-center mb-3">
                        <Col md={6}>
                          <div className="d-flex align-items-center">
                            <div className="me-3">
                              <i className="fas fa-graduation-cap fa-2x text-primary"></i>
                            </div>
                            <div>
                              <h6 className="mb-1 fw-bold">{progress.course?.title}</h6>
                              <div className="d-flex align-items-center gap-2">
                                <small className="text-muted">{progress.course?.category}</small>
                                <span>•</span>
                                <small className="text-muted">{progress.course?.type}</small>
                                <span>•</span>
                                {getPlatformBadge(progress.course?.materials)}
                              </div>
                            </div>
                          </div>
                        </Col>
                        <Col md={3}>
                          <div className="text-center">
                            <div className="h4 mb-1 text-primary">
                              {progress.completionPercentage?.toFixed(1)}%
                            </div>
                            <small className="text-muted">Completion</small>
                          </div>
                        </Col>
                        <Col md={3}>
                          <div className="text-center">
                            <div className="h6 mb-1">
                              {formatTime(progress.timeSpentMinutes)}
                            </div>
                            <small className="text-muted">Time Spent</small>
                          </div>
                        </Col>
                      </Row>

                      {/* Progress Bar */}
                      <Row className="mb-3">
                        <Col>
                          <div className="d-flex justify-content-between align-items-center mb-2">
                            <small className="text-muted">Progress</small>
                            <div className="d-flex align-items-center gap-2">
                              {getStatusBadge(progress.status)}
                              {getGradeBadge(progress.grade)}
                            </div>
                          </div>
                          <ProgressBar 
                            now={progress.completionPercentage || 0} 
                            variant={getProgressVariant(progress.completionPercentage || 0)}
                            className="mb-2"
                            style={{ height: '12px' }}
                          />
                          <div className="d-flex justify-content-between align-items-center">
                            <small className="text-muted">
                              Started: {progress.startedAt ? 
                                new Date(progress.startedAt).toLocaleDateString() : 
                                'N/A'
                              }
                            </small>
                            <small className="text-muted">
                              Last accessed: {progress.lastAccessedAt ? 
                                new Date(progress.lastAccessedAt).toLocaleDateString() : 
                                'N/A'
                              }
                            </small>
                          </div>
                        </Col>
                      </Row>

                      {/* Quiz Score */}
                      {progress.quizScore && (
                        <Row className="mb-3">
                          <Col>
                            <div className="bg-light p-3 rounded">
                              <div className="d-flex justify-content-between align-items-center">
                                <div>
                                  <strong>Quiz Score</strong>
                                  <br />
                                  <small className="text-muted">
                                    {progress.quizScore}/{progress.maxQuizScore} 
                                    ({((progress.quizScore / progress.maxQuizScore) * 100).toFixed(1)}%)
                                  </small>
                                </div>
                                <div className="text-end">
                                  <div className="h5 mb-0 text-success">
                                    {((progress.quizScore / progress.maxQuizScore) * 100).toFixed(1)}%
                                  </div>
                                  <small className="text-muted">Score</small>
                                </div>
                              </div>
                            </div>
                          </Col>
                        </Row>
                      )}

                      {/* Actions */}
                      <Row>
                        <Col>
                          <div className="d-flex gap-2">
                            <Button 
                              size="sm" 
                              variant="outline-primary"
                              title="Continue Learning"
                            >
                              <i className="fas fa-play me-1"></i>
                              Continue
                            </Button>
                            {progress.course?.materials && progress.course.materials.startsWith('http') && (
                              <Button 
                                size="sm" 
                                variant="outline-info"
                                title="Go to Course"
                                onClick={() => window.open(progress.course.materials, '_blank')}
                              >
                                <i className="fas fa-external-link-alt me-1"></i>
                                Go to Course
                              </Button>
                            )}
                            <Button 
                              size="sm" 
                              variant="outline-secondary"
                              title="View Details"
                            >
                              <i className="fas fa-eye me-1"></i>
                              Details
                            </Button>
                          </div>
                        </Col>
                      </Row>
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Progress;
