import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ProgressBar, Badge, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import apiClient from '../services/AuthService';

const Dashboard = ({ user }) => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalCourses: 0,
    enrolledCourses: 0,
    completedCourses: 0,
    inProgressCourses: 0,
    certificates: 0,
    notifications: 0
  });
  const [recentCourses, setRecentCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Fetch courses
      const coursesResponse = await apiClient.get('/courses');
      const courses = coursesResponse.data;

      // Fetch enrollments for current user
      const enrollmentsResponse = await apiClient.get('/enrollments');
      const enrollments = enrollmentsResponse.data.filter(e => e.user.id === user.id);

      // Fetch progress for current user
      const progressResponse = await apiClient.get('/progress');
      const progress = progressResponse.data.filter(p => p.user.id === user.id);

      // Calculate stats
      const completedCourses = progress.filter(p => p.status === 'COMPLETED').length;
      const inProgressCourses = progress.filter(p => p.status === 'IN_PROGRESS').length;

      setStats({
        totalCourses: courses.length,
        enrolledCourses: enrollments.length,
        completedCourses,
        inProgressCourses,
        certificates: completedCourses, // Assuming 1 certificate per completed course
        notifications: 3 // Mock data
      });

      // Set recent courses (last 5 enrolled)
      setRecentCourses(enrollments.slice(0, 5));

    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoleDisplayName = (role) => {
    switch (role) {
      case 'ADMIN':
        return 'Administrator';
      case 'MANAGER':
        return 'Manager';
      case 'TRAINER':
        return 'Trainer';
      case 'EMPLOYEE':
        return 'Employee';
      default:
        return role;
    }
  };

  const getProgressPercentage = (courseId) => {
    // Mock progress data - in real app, this would come from API
    const progress = Math.floor(Math.random() * 100);
    return progress;
  };

  const handleQuickAction = (action) => {
    switch (action) {
      case 'browse':
        navigate('/courses');
        break;
      case 'progress':
        navigate('/progress');
        break;
      case 'certificates':
        navigate('/certificates');
        break;
      case 'notifications':
        navigate('/notifications');
        break;
      default:
        break;
    }
  };

  if (loading) {
    return (
      <Container>
        <div className="loading-container">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-tachometer-alt me-2"></i>
            Welcome back, {user.firstName}!
          </h1>
          <p className="text-muted">
            {getRoleDisplayName(user.role)} • {user.department}
          </p>
        </Col>
      </Row>

      {/* Statistics Cards */}
      <Row className="mb-4">
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.totalCourses}</h3>
            <p className="mb-0">Total Courses</p>
          </div>
        </Col>
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.enrolledCourses}</h3>
            <p className="mb-0">Enrolled Courses</p>
          </div>
        </Col>
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.completedCourses}</h3>
            <p className="mb-0">Completed Courses</p>
          </div>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.inProgressCourses}</h3>
            <p className="mb-0">In Progress</p>
          </div>
        </Col>
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.certificates}</h3>
            <p className="mb-0">Certificates</p>
          </div>
        </Col>
        <Col md={4} className="mb-3">
          <div className="stat-card">
            <h3>{stats.notifications}</h3>
            <p className="mb-0">Notifications</p>
          </div>
        </Col>
      </Row>

      {/* Recent Courses */}
      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-book me-2"></i>
                Recent Courses
              </h5>
            </Card.Header>
            <Card.Body>
              {recentCourses.length > 0 ? (
                recentCourses.map((enrollment, index) => (
                  <div key={index} className="mb-3 p-3 border rounded">
                    <div className="d-flex justify-content-between align-items-center mb-2">
                      <h6 className="mb-0">{enrollment.course.title}</h6>
                      <Badge bg={enrollment.status === 'COMPLETED' ? 'success' : 
                                enrollment.status === 'IN_PROGRESS' ? 'warning' : 'secondary'}>
                        {enrollment.status}
                      </Badge>
                    </div>
                    <p className="text-muted small mb-2">{enrollment.course.description}</p>
                    <div className="d-flex justify-content-between align-items-center">
                      <small className="text-muted">
                        {enrollment.course.category} • {enrollment.course.type}
                      </small>
                      <div className="d-flex align-items-center">
                        <small className="me-2">Progress:</small>
                        <ProgressBar 
                          now={getProgressPercentage(enrollment.course.id)} 
                          style={{ width: '100px' }}
                        />
                        <small className="ms-2">{getProgressPercentage(enrollment.course.id)}%</small>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="empty-state">
                  <i className="fas fa-book-open"></i>
                  <p>No courses enrolled yet.</p>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Quick Actions */}
      <Row className="mt-4">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-bolt me-2"></i>
                Quick Actions
              </h5>
            </Card.Header>
            <Card.Body>
              <Row>
                <Col md={3} className="mb-3">
                  <Button 
                    variant="outline-primary" 
                    className="w-100 h-100 d-flex flex-column align-items-center justify-content-center p-3"
                    onClick={() => handleQuickAction('browse')}
                    style={{ minHeight: '120px' }}
                  >
                    <i className="fas fa-search fa-2x mb-2"></i>
                    <span>Browse Courses</span>
                  </Button>
                </Col>
                <Col md={3} className="mb-3">
                  <Button 
                    variant="outline-success" 
                    className="w-100 h-100 d-flex flex-column align-items-center justify-content-center p-3"
                    onClick={() => handleQuickAction('progress')}
                    style={{ minHeight: '120px' }}
                  >
                    <i className="fas fa-chart-line fa-2x mb-2"></i>
                    <span>View Progress</span>
                  </Button>
                </Col>
                <Col md={3} className="mb-3">
                  <Button 
                    variant="outline-warning" 
                    className="w-100 h-100 d-flex flex-column align-items-center justify-content-center p-3"
                    onClick={() => handleQuickAction('certificates')}
                    style={{ minHeight: '120px' }}
                  >
                    <i className="fas fa-certificate fa-2x mb-2"></i>
                    <span>My Certificates</span>
                  </Button>
                </Col>
                <Col md={3} className="mb-3">
                  <Button 
                    variant="outline-info" 
                    className="w-100 h-100 d-flex flex-column align-items-center justify-content-center p-3"
                    onClick={() => handleQuickAction('notifications')}
                    style={{ minHeight: '120px' }}
                  >
                    <i className="fas fa-bell fa-2x mb-2"></i>
                    <span>Notifications</span>
                  </Button>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
