import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Modal, Form, Alert } from 'react-bootstrap';
import apiClient from '../services/apiClient';

const Enrollments = ({ user }) => {
  const [enrollments, setEnrollments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showEnrollModal, setShowEnrollModal] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [enrollmentType, setEnrollmentType] = useState('OPTIONAL');
  const [filterStatus, setFilterStatus] = useState('');
  const [filterPlatform, setFilterPlatform] = useState('');

  useEffect(() => {
    loadEnrollments();
    loadCourses();
    loadUsers();
  }, []);

  const loadEnrollments = async () => {
    try {
      setLoading(true);
      // For admin/manager, get all enrollments; for others, get user-specific
      const endpoint = user.role === 'ADMIN' || user.role === 'MANAGER' ? '/enrollments' : `/enrollments/user/${user.id}`;
      const response = await apiClient.get(endpoint);
      setEnrollments(response.data);
    } catch (err) {
      setError('Failed to load enrollments');
      console.error(err);
      // Set default enrollments if API fails
      setEnrollments([
        {
          id: 1,
          status: 'COMPLETED',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 45 * 24 * 60 * 60 * 1000),
          completedAt: new Date(Date.now() - 5 * 24 * 60 * 60 * 1000),
          completionPercentage: 100.0,
          grade: 'A+',
          certificateEarned: true,
          notes: 'Completed successfully',
          course: {
            id: 1,
            title: 'Java Programming Fundamentals',
            description: 'Learn core Java concepts and object-oriented programming',
            category: 'TECHNICAL',
            type: 'VIRTUAL',
            durationHours: 40,
            materials: 'https://www.udemy.com/course/java-programming-tutorial-for-beginners/',
            prerequisites: 'Basic computer knowledge',
            isMandatory: true,
            maxParticipants: 50,
            difficultyLevel: 'BEGINNER',
            completionRate: 85,
            averageRating: 4.8,
            reviewCount: 1250
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 2,
          status: 'COMPLETED',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
          completedAt: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
          completionPercentage: 100.0,
          grade: 'A',
          certificateEarned: true,
          notes: 'Required for role',
          course: {
            id: 2,
            title: 'Spring Boot Development',
            description: 'Master Spring Boot framework for building web applications',
            category: 'TECHNICAL',
            type: 'HYBRID',
            durationHours: 60,
            materials: 'https://www.udemy.com/course/spring-boot-tutorial-for-beginners/',
            prerequisites: 'Java Programming Fundamentals',
            isMandatory: true,
            maxParticipants: 30,
            difficultyLevel: 'INTERMEDIATE',
            completionRate: 78,
            averageRating: 4.6,
            reviewCount: 890
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 3,
          status: 'IN_PROGRESS',
          type: 'OPTIONAL',
          enrolledAt: new Date(Date.now() - 15 * 24 * 60 * 60 * 1000),
          completedAt: null,
          completionPercentage: 65.0,
          grade: null,
          certificateEarned: false,
          notes: 'Career development',
          course: {
            id: 3,
            title: 'React.js for Beginners',
            description: 'Learn React.js for building modern web applications',
            category: 'TECHNICAL',
            type: 'SELF_PACED',
            durationHours: 35,
            materials: 'https://www.udemy.com/course/react-the-complete-guide-incl-redux/',
            prerequisites: 'JavaScript basics',
            isMandatory: false,
            maxParticipants: 40,
            difficultyLevel: 'BEGINNER',
            completionRate: 82,
            averageRating: 4.7,
            reviewCount: 2100
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 4,
          status: 'IN_PROGRESS',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 10 * 24 * 60 * 60 * 1000),
          completedAt: null,
          completionPercentage: 45.0,
          grade: null,
          certificateEarned: false,
          notes: 'Compliance requirement',
          course: {
            id: 4,
            title: 'Leadership Skills',
            description: 'Develop essential leadership and management skills',
            category: 'LEADERSHIP',
            type: 'IN_PERSON',
            durationHours: 20,
            materials: 'Internal materials',
            prerequisites: 'None',
            isMandatory: false,
            maxParticipants: 25,
            difficultyLevel: 'INTERMEDIATE',
            completionRate: 92,
            averageRating: 4.5,
            reviewCount: 156
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const loadCourses = async () => {
    try {
      const response = await apiClient.get('/courses');
      setCourses(response.data);
    } catch (err) {
      console.error('Failed to load courses:', err);
    }
  };

  const loadUsers = async () => {
    try {
      const response = await apiClient.get('/users');
      setUsers(response.data);
    } catch (err) {
      console.error('Failed to load users:', err);
    }
  };

  const handleEnroll = async () => {
    try {
      const enrollmentData = {
        userId: user.id,
        courseId: parseInt(selectedCourse),
        type: enrollmentType
      };

      await apiClient.post('/enrollments', enrollmentData);
      setShowEnrollModal(false);
      setSelectedCourse('');
      setEnrollmentType('OPTIONAL');
      loadEnrollments(); // Reload enrollments
    } catch (err) {
      setError('Failed to enroll in course');
      console.error(err);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      'ENROLLED': 'primary',
      'IN_PROGRESS': 'warning',
      'COMPLETED': 'success',
      'DROPPED': 'danger',
      'FAILED': 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status.replace('_', ' ')}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      'MANDATORY': 'danger',
      'OPTIONAL': 'info',
      'ASSIGNED': 'warning'
    };
    return <Badge bg={variants[type] || 'secondary'}>{type.replace('_', ' ')}</Badge>;
  };

  const getPlatformBadge = (materials) => {
    if (!materials) return <Badge bg="secondary">Internal</Badge>;

    if (materials.includes('udemy.com')) {
      return <Badge bg="purple">Udemy</Badge>;
    } else if (materials.includes('hackerrank.com')) {
      return <Badge bg="orange">HackerRank</Badge>;
    } else if (materials.includes('intershala.com')) {
      return <Badge bg="blue">Internshala</Badge>;
    } else if (materials.includes('coursera.org')) {
      return <Badge bg="teal">Coursera</Badge>;
    } else if (materials.includes('edx.org')) {
      return <Badge bg="dark">edX</Badge>;
    } else if (materials.includes('http')) {
      return <Badge bg="info">External</Badge>;
    }
    return <Badge bg="secondary">Internal</Badge>;
  };

  const getDifficultyBadge = (difficulty) => {
    const variants = {
      'BEGINNER': 'success',
      'INTERMEDIATE': 'warning',
      'ADVANCED': 'danger'
    };
    return <Badge bg={variants[difficulty] || 'secondary'}>{difficulty}</Badge>;
  };

  const getUserInfo = (userId) => {
    const user = users.find(u => u.id === userId);
    return user ? `${user.firstName} ${user.lastName}` : 'Unknown User';
  };

  const getUserDetails = (userId) => {
    const user = users.find(u => u.id === userId);
    return user ? {
      name: `${user.firstName} ${user.lastName}`,
      email: user.email,
      department: user.department,
      role: user.role,
      phone: user.phoneNumber
    } : null;
  };

  const filteredEnrollments = enrollments.filter(enrollment => {
    const matchesStatus = !filterStatus || enrollment.status === filterStatus;
    const matchesPlatform = !filterPlatform || 
      (enrollment.course?.materials && enrollment.course.materials.includes(filterPlatform));
    return matchesStatus && matchesPlatform;
  });

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-user-graduate me-2"></i>
            {user.role === 'ADMIN' || user.role === 'MANAGER' ? 'All Enrollments' : 'My Enrollments'}
          </h1>
          <p className="text-muted">
            {user.role === 'ADMIN' || user.role === 'MANAGER' 
              ? 'Manage all course enrollments across the organization' 
              : 'Manage your course enrollments and track progress'
            }
          </p>
        </Col>
        <Col xs="auto">
          <Button 
            variant="primary" 
            onClick={() => setShowEnrollModal(true)}
            className="d-flex align-items-center"
          >
            <i className="fas fa-plus me-2"></i>
            Enroll in Course
          </Button>
        </Col>
      </Row>

      {/* Filters */}
      <Row className="mb-4">
        <Col md={4}>
          <Form.Select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
          >
            <option value="">All Statuses</option>
            <option value="ENROLLED">Enrolled</option>
            <option value="IN_PROGRESS">In Progress</option>
            <option value="COMPLETED">Completed</option>
            <option value="DROPPED">Dropped</option>
            <option value="FAILED">Failed</option>
          </Form.Select>
        </Col>
        <Col md={4}>
          <Form.Select
            value={filterPlatform}
            onChange={(e) => setFilterPlatform(e.target.value)}
          >
            <option value="">All Platforms</option>
            <option value="udemy.com">Udemy</option>
            <option value="hackerrank.com">HackerRank</option>
            <option value="intershala.com">Internshala</option>
            <option value="coursera.org">Coursera</option>
            <option value="edx.org">edX</option>
          </Form.Select>
        </Col>
        <Col md={4}>
          <Button 
            variant="outline-secondary" 
            onClick={() => {
              setFilterStatus('');
              setFilterPlatform('');
            }}
          >
            Clear Filters
          </Button>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Enrollment History ({filteredEnrollments.length} enrollments)
              </h5>
            </Card.Header>
            <Card.Body>
              {filteredEnrollments.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-user-graduate fa-3x text-muted mb-3"></i>
                  <h5>No Enrollments Found</h5>
                  <p className="text-muted">
                    {filterStatus || filterPlatform 
                      ? 'No enrollments match the selected filters.' 
                      : 'No enrollments found.'
                    }
                  </p>
                  {(filterStatus || filterPlatform) && (
                    <Button variant="outline-primary" onClick={() => {
                      setFilterStatus('');
                      setFilterPlatform('');
                    }}>
                      Clear Filters
                    </Button>
                  )}
                </div>
              ) : (
                <Table responsive striped>
                  <thead>
                    <tr>
                      {(user.role === 'ADMIN' || user.role === 'MANAGER') && (
                        <th>User</th>
                      )}
                      <th>Course Details</th>
                      <th>Platform</th>
                      <th>Type</th>
                      <th>Status</th>
                      <th>Progress</th>
                      <th>Enrolled Date</th>
                      <th>Completion Date</th>
                      <th>Grade</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredEnrollments.map((enrollment) => {
                      const userDetails = getUserDetails(enrollment.user.id);
                      return (
                        <tr key={enrollment.id}>
                          {(user.role === 'ADMIN' || user.role === 'MANAGER') && (
                            <td>
                              <div>
                                <strong>{userDetails?.name}</strong>
                                <br />
                                <small className="text-muted">
                                  {userDetails?.email}
                                  <br />
                                  {userDetails?.department} • {userDetails?.role}
                                </small>
                              </div>
                            </td>
                          )}
                          <td>
                            <div>
                              <strong>{enrollment.course?.title}</strong>
                              <br />
                              <small className="text-muted">
                                {enrollment.course?.description}
                                <br />
                                {enrollment.course?.category} • {enrollment.course?.type}
                                <br />
                                Duration: {enrollment.course?.durationHours} hours
                                <br />
                                Difficulty: {getDifficultyBadge(enrollment.course?.difficultyLevel)}
                                <br />
                                Completion Rate: {enrollment.course?.completionRate}% • 
                                Rating: {enrollment.course?.averageRating} ⭐ ({enrollment.course?.reviewCount} reviews)
                              </small>
                            </div>
                          </td>
                          <td>
                            {getPlatformBadge(enrollment.course?.materials)}
                          </td>
                          <td>{getTypeBadge(enrollment.type)}</td>
                          <td>{getStatusBadge(enrollment.status)}</td>
                          <td>
                            <div className="d-flex align-items-center">
                              <div className="me-2" style={{ width: '60px' }}>
                                <small>{enrollment.completionPercentage?.toFixed(1)}%</small>
                              </div>
                              <div style={{ width: '80px' }}>
                                <div className="progress" style={{ height: '8px' }}>
                                  <div 
                                    className="progress-bar" 
                                    style={{ width: `${enrollment.completionPercentage || 0}%` }}
                                  ></div>
                                </div>
                              </div>
                            </div>
                          </td>
                          <td>
                            {enrollment.enrolledAt ? 
                              new Date(enrollment.enrolledAt).toLocaleDateString() : 
                              'N/A'
                            }
                          </td>
                          <td>
                            {enrollment.completedAt ? 
                              new Date(enrollment.completedAt).toLocaleDateString() : 
                              'N/A'
                            }
                          </td>
                          <td>
                            {enrollment.grade || 'N/A'}
                          </td>
                          <td>
                            <Button 
                              size="sm" 
                              variant="outline-primary" 
                              className="me-1"
                              title="View Details"
                            >
                              <i className="fas fa-eye"></i>
                            </Button>
                            {enrollment.status === 'ENROLLED' && (
                              <Button 
                                size="sm" 
                                variant="outline-success"
                                className="me-1"
                                title="Start Course"
                              >
                                <i className="fas fa-play"></i>
                              </Button>
                            )}
                            {enrollment.course?.materials && enrollment.course.materials.startsWith('http') && (
                              <Button 
                                size="sm" 
                                variant="outline-info"
                                title="Go to Course"
                                onClick={() => window.open(enrollment.course.materials, '_blank')}
                              >
                                <i className="fas fa-external-link-alt"></i>
                              </Button>
                            )}
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Enroll Modal */}
      <Modal show={showEnrollModal} onHide={() => setShowEnrollModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Enroll in Course</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Select Course</Form.Label>
              <Form.Select 
                value={selectedCourse} 
                onChange={(e) => setSelectedCourse(e.target.value)}
              >
                <option value="">Choose a course...</option>
                {courses
                  .filter(course => !enrollments.some(enrollment => enrollment.course?.id === course.id))
                  .map(course => (
                    <option key={course.id} value={course.id}>
                      {course.title} - {course.category} ({course.type})
                    </option>
                  ))
                }
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Enrollment Type</Form.Label>
              <Form.Select 
                value={enrollmentType} 
                onChange={(e) => setEnrollmentType(e.target.value)}
              >
                <option value="OPTIONAL">Optional</option>
                <option value="ASSIGNED">Assigned</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEnrollModal(false)}>
            Cancel
          </Button>
          <Button 
            variant="primary" 
            onClick={handleEnroll}
            disabled={!selectedCourse}
          >
            Enroll
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Enrollments;
