import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert, Badge } from 'react-bootstrap';

const Feedback = ({ user }) => {
  const [feedbackList, setFeedbackList] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    courseId: '',
    contentRating: 5,
    instructorRating: 5,
    facilityRating: 5,
    overallSatisfaction: 5,
    wouldRecommend: true,
    comments: '',
    suggestions: ''
  });

  useEffect(() => {
    loadFeedback();
    loadCourses();
  }, []);

  const loadFeedback = async () => {
    try {
      setLoading(true);
      // Mock data for now
      const mockFeedback = [
        {
          id: 1,
          courseName: 'Java Programming Fundamentals',
          userName: 'John Doe',
          userEmail: 'john.doe@sasken.com',
          contentRating: 5,
          instructorRating: 4,
          facilityRating: 5,
          overallSatisfaction: 4,
          wouldRecommend: true,
          comments: 'Excellent course! The instructor was very knowledgeable and the content was well-structured.',
          suggestions: 'Could include more hands-on exercises.',
          submittedAt: '2024-01-15T10:30:00Z',
          status: 'SUBMITTED'
        },
        {
          id: 2,
          courseName: 'Spring Boot Development',
          userName: 'Jane Smith',
          userEmail: 'jane.smith@sasken.com',
          contentRating: 4,
          instructorRating: 5,
          facilityRating: 4,
          overallSatisfaction: 4,
          wouldRecommend: true,
          comments: 'Great practical examples and real-world applications.',
          suggestions: 'More advanced topics would be beneficial.',
          submittedAt: '2024-01-20T14:15:00Z',
          status: 'REVIEWED'
        }
      ];
      setFeedbackList(mockFeedback);
    } catch (err) {
      setError('Failed to load feedback');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadCourses = async () => {
    try {
      // Mock courses data
      const mockCourses = [
        { id: 1, title: 'Java Programming Fundamentals' },
        { id: 2, title: 'Spring Boot Development' },
        { id: 3, title: 'React.js Frontend Development' },
        { id: 4, title: 'Python for Data Science' }
      ];
      setCourses(mockCourses);
    } catch (err) {
      console.error('Failed to load courses:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const selectedCourse = courses.find(c => c.id == formData.courseId);
      const newFeedback = {
        id: Date.now(),
        courseName: selectedCourse.title,
        userName: `${user.firstName} ${user.lastName}`,
        userEmail: user.email,
        ...formData,
        submittedAt: new Date().toISOString(),
        status: 'SUBMITTED'
      };

      setFeedbackList([newFeedback, ...feedbackList]);
      setShowForm(false);
      setFormData({
        courseId: '',
        contentRating: 5,
        instructorRating: 5,
        facilityRating: 5,
        overallSatisfaction: 5,
        wouldRecommend: true,
        comments: '',
        suggestions: ''
      });
    } catch (err) {
      setError('Failed to submit feedback');
      console.error(err);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      'SUBMITTED': 'primary',
      'REVIEWED': 'info',
      'APPROVED': 'success',
      'REJECTED': 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getRatingStars = (rating) => {
    return '★'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-comments me-2"></i>
            Course Feedback
          </h1>
          <p className="text-muted">Share your thoughts and suggestions about courses</p>
        </Col>
        <Col xs="auto">
          <Button 
            variant="primary" 
            onClick={() => setShowForm(true)}
            className="d-flex align-items-center"
          >
            <i className="fas fa-plus me-2"></i>
            Submit Feedback
          </Button>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      {/* Feedback Form Modal */}
      {showForm && (
        <Card className="mb-4">
          <Card.Header>
            <h5 className="mb-0">
              <i className="fas fa-edit me-2"></i>
              Submit Course Feedback
            </h5>
          </Card.Header>
          <Card.Body>
            <Form onSubmit={handleSubmit}>
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Select Course</Form.Label>
                    <Form.Select 
                      value={formData.courseId} 
                      onChange={(e) => setFormData({...formData, courseId: e.target.value})}
                      required
                    >
                      <option value="">Choose a course...</option>
                      {courses.map(course => (
                        <option key={course.id} value={course.id}>
                          {course.title}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col md={3}>
                  <Form.Group className="mb-3">
                    <Form.Label>Content Quality</Form.Label>
                    <Form.Select 
                      value={formData.contentRating} 
                      onChange={(e) => setFormData({...formData, contentRating: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(rating => (
                        <option key={rating} value={rating}>
                          {rating} {rating === 1 ? 'Star' : 'Stars'}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={3}>
                  <Form.Group className="mb-3">
                    <Form.Label>Instructor Quality</Form.Label>
                    <Form.Select 
                      value={formData.instructorRating} 
                      onChange={(e) => setFormData({...formData, instructorRating: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(rating => (
                        <option key={rating} value={rating}>
                          {rating} {rating === 1 ? 'Star' : 'Stars'}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={3}>
                  <Form.Group className="mb-3">
                    <Form.Label>Facility Quality</Form.Label>
                    <Form.Select 
                      value={formData.facilityRating} 
                      onChange={(e) => setFormData({...formData, facilityRating: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(rating => (
                        <option key={rating} value={rating}>
                          {rating} {rating === 1 ? 'Star' : 'Stars'}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={3}>
                  <Form.Group className="mb-3">
                    <Form.Label>Overall Satisfaction</Form.Label>
                    <Form.Select 
                      value={formData.overallSatisfaction} 
                      onChange={(e) => setFormData({...formData, overallSatisfaction: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(rating => (
                        <option key={rating} value={rating}>
                          {rating} {rating === 1 ? 'Star' : 'Stars'}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>

              <Form.Group className="mb-3">
                <Form.Check 
                  type="checkbox" 
                  label="Would you recommend this course to others?"
                  checked={formData.wouldRecommend}
                  onChange={(e) => setFormData({...formData, wouldRecommend: e.target.checked})}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Comments</Form.Label>
                <Form.Control 
                  as="textarea" 
                  rows={3}
                  value={formData.comments}
                  onChange={(e) => setFormData({...formData, comments: e.target.value})}
                  placeholder="Share your experience with this course..."
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Suggestions for Improvement</Form.Label>
                <Form.Control 
                  as="textarea" 
                  rows={3}
                  value={formData.suggestions}
                  onChange={(e) => setFormData({...formData, suggestions: e.target.value})}
                  placeholder="Any suggestions to improve this course..."
                />
              </Form.Group>

              <div className="d-flex gap-2">
                <Button type="submit" variant="primary">
                  Submit Feedback
                </Button>
                <Button variant="secondary" onClick={() => setShowForm(false)}>
                  Cancel
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
      )}

      {/* Feedback List */}
      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Recent Feedback
              </h5>
            </Card.Header>
            <Card.Body>
              {feedbackList.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-comments fa-3x text-muted mb-3"></i>
                  <h5>No Feedback Yet</h5>
                  <p className="text-muted">Be the first to share your thoughts about courses.</p>
                </div>
              ) : (
                <div>
                  {feedbackList.map((feedback) => (
                    <div key={feedback.id} className="mb-4 p-3 border rounded">
                      <Row>
                        <Col md={8}>
                          <h6 className="mb-1">{feedback.courseName}</h6>
                          <p className="text-muted mb-2">
                            <small>
                              <i className="fas fa-user me-1"></i>
                              {feedback.userName} ({feedback.userEmail})
                            </small>
                          </p>
                          <div className="mb-2">
                            <small className="text-muted">Ratings:</small>
                            <div className="d-flex gap-3 mt-1">
                              <span>Content: <span style={{color: '#ffc107'}}>{getRatingStars(feedback.contentRating)}</span></span>
                              <span>Instructor: <span style={{color: '#ffc107'}}>{getRatingStars(feedback.instructorRating)}</span></span>
                              <span>Facility: <span style={{color: '#ffc107'}}>{getRatingStars(feedback.facilityRating)}</span></span>
                              <span>Overall: <span style={{color: '#ffc107'}}>{getRatingStars(feedback.overallSatisfaction)}</span></span>
                            </div>
                          </div>
                          {feedback.comments && (
                            <div className="mb-2">
                              <strong>Comments:</strong>
                              <p className="mb-1">{feedback.comments}</p>
                            </div>
                          )}
                          {feedback.suggestions && (
                            <div className="mb-2">
                              <strong>Suggestions:</strong>
                              <p className="mb-1">{feedback.suggestions}</p>
                            </div>
                          )}
                        </Col>
                        <Col md={4} className="text-end">
                          <div className="mb-2">
                            {getStatusBadge(feedback.status)}
                          </div>
                          <small className="text-muted">
                            {new Date(feedback.submittedAt).toLocaleDateString()}
                          </small>
                          {feedback.wouldRecommend && (
                            <div className="mt-2">
                              <Badge bg="success">
                                <i className="fas fa-thumbs-up me-1"></i>
                                Would Recommend
                              </Badge>
                            </div>
                          )}
                        </Col>
                      </Row>
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Feedback;
