import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Badge, Alert } from 'react-bootstrap';
import html2canvas from 'html2canvas';
import apiClient from '../services/apiClient';

const Certificates = ({ user }) => {
  const [certificates, setCertificates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadCertificates();
  }, []);

  const loadCertificates = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get(`/certificates/user/${user.id}`);
      setCertificates(response.data);
    } catch (err) {
      setError('Failed to load certificates');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const downloadCertificate = async (certificate) => {
    try {
      // Create certificate HTML
      const certificateHTML = `
        <div id="certificate-${certificate.id}" style="
          width: 900px; 
          height: 650px; 
          background: #fff;
          color: #333;
          padding: 40px;
          font-family: 'Arial', sans-serif;
          text-align: center;
          border-radius: 20px;
          box-shadow: 0 10px 30px rgba(0,0,0,0.15);
          border: 8px solid #e5e5e5;
          position: relative;
        ">
          <div style="border: 4px solid #b49c73; padding: 30px; height: 100%; display: flex; flex-direction: column; justify-content: space-between; background: #f9f6f1;">
            <div style="margin-bottom: 20px;">
              <img src='data:image/svg+xml;base64,' alt='SkillSync Logo' style='width: 80px; height: 80px; margin-bottom: 10px;' />
              <h1 style="font-size: 38px; color: #b49c73; margin-bottom: 10px;">Certificate of Completion</h1>
              <p style="font-size: 20px; margin-bottom: 30px; color: #555;">This is to certify that</p>
              <h2 style="font-size: 30px; color: #333; margin-bottom: 20px;">${certificate.user?.firstName} ${certificate.user?.lastName}</h2>
              <p style="font-size: 20px; margin-bottom: 30px; color: #555;">has successfully completed the course</p>
              <h3 style="font-size: 26px; color: #b49c73; margin-bottom: 30px;">${certificate.course?.title}</h3>
            </div>
            <div style="margin: 20px 0;">
              <p style="font-size: 18px; margin-bottom: 10px;">Grade: <span style="color: #b49c73; font-weight: bold;">${certificate.grade}</span></p>
              <p style="font-size: 18px; margin-bottom: 10px;">Completed on: ${new Date(certificate.completionDate).toLocaleDateString()}</p>
              <p style="font-size: 16px; margin-bottom: 10px;">Certificate ID: <span style='color: #b49c73;'>${certificate.certificateNumber}</span></p>
            </div>
            <div style="margin-top: 30px;">
              <p style="font-size: 18px; font-style: italic; color: #555;">"Excellence in learning and dedication to growth"</p>
              <div style="display: flex; justify-content: space-between; align-items: center; margin-top: 40px;">
                <div style="text-align: left;">
                  <p style="font-size: 16px; margin-bottom: 5px;">_________________</p>
                  <p style="font-size: 14px; color: #888;">Course Instructor</p>
                </div>
                <div style="text-align: right;">
                  <p style="font-size: 16px; margin-bottom: 5px;">_________________</p>
                  <p style="font-size: 14px; color: #888;">Program Director</p>
                </div>
              </div>
            </div>
            <div style="position: absolute; bottom: 30px; right: 40px;">
              <span style="font-size: 12px; color: #b49c73;">SkillSync Training Tracker</span>
            </div>
          </div>
        </div>
      `;

      // Create a temporary div to render the certificate
      const tempDiv = document.createElement('div');
      tempDiv.innerHTML = certificateHTML;
      tempDiv.style.position = 'absolute';
      tempDiv.style.left = '-9999px';
      document.body.appendChild(tempDiv);

      // Convert to canvas and download
      const canvas = await html2canvas(tempDiv.querySelector(`#certificate-${certificate.id}`));
      const link = document.createElement('a');
      link.download = `certificate-${certificate.course?.title?.replace(/\s+/g, '-')}.png`;
      link.href = canvas.toDataURL();
      link.click();

      // Clean up
      document.body.removeChild(tempDiv);
    } catch (error) {
      console.error('Error downloading certificate:', error);
      alert('Failed to download certificate. Please try again.');
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      'ISSUED': 'success',
      'PENDING': 'warning',
      'REVOKED': 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-certificate me-2"></i>
            My Certificates
          </h1>
          <p className="text-muted">View and download your earned certificates</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Earned Certificates
              </h5>
            </Card.Header>
            <Card.Body>
              {certificates.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-certificate fa-3x text-muted mb-3"></i>
                  <h5>No Certificates Yet</h5>
                  <p className="text-muted">Complete courses to earn certificates.</p>
                </div>
              ) : (
                <div>
                  {certificates.map((certificate) => (
                    <div key={certificate.id} className="mb-3 p-3 border rounded">
                      <Row className="align-items-center">
                        <Col md={4}>
                          <h6 className="mb-1">{certificate.course?.title}</h6>
                          <small className="text-muted">Certificate ID: {certificate.certificateNumber}</small>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            <strong style={{ color: certificate.grade === 'A+' ? '#28a745' : '#007bff' }}>
                              {certificate.grade}
                            </strong>
                            <br />
                            <small className="text-muted">Grade</small>
                          </div>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            {new Date(certificate.completionDate).toLocaleDateString()}
                            <br />
                            <small className="text-muted">Completed</small>
                          </div>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            {getStatusBadge(certificate.status)}
                          </div>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => downloadCertificate(certificate)}
                            >
                              <i className="fas fa-download me-1"></i>
                              Download
                            </Button>
                          </div>
                        </Col>
                      </Row>
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Certificate Statistics */}
      <Row className="mt-4">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-chart-pie me-2"></i>
                Certificate Statistics
              </h5>
            </Card.Header>
            <Card.Body>
              <Row>
                <Col md={3} className="text-center">
                  <h4 className="text-primary">{certificates.length}</h4>
                  <p className="text-muted mb-0">Total Certificates</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-success">{certificates.filter(c => c.status === 'ISSUED').length}</h4>
                  <p className="text-muted mb-0">Issued</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-warning">{certificates.filter(c => c.grade === 'A+').length}</h4>
                  <p className="text-muted mb-0">A+ Grades</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-info">{certificates.filter(c => c.grade === 'A').length}</h4>
                  <p className="text-muted mb-0">A Grades</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Certificates;
