import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Alert } from 'react-bootstrap';
import apiClient from '../services/apiClient';

const Reports = ({ user }) => {
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [progress, setProgress] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedReport, setSelectedReport] = useState('overview');

  useEffect(() => {
    loadReportData();
  }, []);

  const loadReportData = async () => {
    try {
      setLoading(true);

      const [usersResponse, coursesResponse, enrollmentsResponse, progressResponse] = await Promise.all([
        apiClient.get('/users'),
        apiClient.get('/courses'),
        apiClient.get('/enrollments'),
        apiClient.get('/progress')
      ]);

      setUsers(usersResponse.data);
      setCourses(coursesResponse.data);
      setEnrollments(enrollmentsResponse.data);
      setProgress(progressResponse.data);
    } catch (err) {
      setError('Failed to load report data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const getAnalytics = () => {
    const totalUsers = users.length;
    const activeUsers = users.filter(u => u.isActive).length;
    const totalCourses = courses.length;
    const totalEnrollments = enrollments.length;
    const completedEnrollments = enrollments.filter(e => e.status === 'COMPLETED').length;
    const inProgressEnrollments = enrollments.filter(e => e.status === 'IN_PROGRESS').length;

    const totalTimeSpent = progress.reduce((sum, p) => sum + (p.timeSpentMinutes || 0), 0);
    const averageCompletion = progress.length > 0 ? 
      progress.reduce((sum, p) => sum + (p.completionPercentage || 0), 0) / progress.length : 0;

    // Department-wise statistics
    const departmentStats = users.reduce((acc, user) => {
      if (!acc[user.department]) {
        acc[user.department] = { count: 0, enrollments: 0, completed: 0 };
      }
      acc[user.department].count++;
      return acc;
    }, {});

    // Add enrollment data to department stats
    enrollments.forEach(enrollment => {
      const user = users.find(u => u.id === enrollment.user.id);
      if (user && departmentStats[user.department]) {
        departmentStats[user.department].enrollments++;
        if (enrollment.status === 'COMPLETED') {
          departmentStats[user.department].completed++;
        }
      }
    });

    // Platform statistics
    const platformStats = {};
    enrollments.forEach(enrollment => {
      const materials = enrollment.course?.materials || '';
      let platform = 'Internal';
      if (materials.includes('udemy.com')) platform = 'Udemy';
      else if (materials.includes('hackerrank.com')) platform = 'HackerRank';
      else if (materials.includes('intershala.com')) platform = 'Internshala';
      else if (materials.includes('coursera.org')) platform = 'Coursera';
      else if (materials.includes('edx.org')) platform = 'edX';
      else if (materials.includes('http')) platform = 'External';

      platformStats[platform] = (platformStats[platform] || 0) + 1;
    });

    // Course category statistics
    const categoryStats = {};
    enrollments.forEach(enrollment => {
      const category = enrollment.course?.category || 'Unknown';
      categoryStats[category] = (categoryStats[category] || 0) + 1;
    });

    return {
      totalUsers,
      activeUsers,
      totalCourses,
      totalEnrollments,
      completedEnrollments,
      inProgressEnrollments,
      totalTimeSpent,
      averageCompletion,
      departmentStats,
      platformStats,
      categoryStats
    };
  };

  const formatTime = (minutes) => {
    if (!minutes) return '0h 0m';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getRoleBadge = (role) => {
    const variants = {
      'ADMIN': 'danger',
      'MANAGER': 'warning',
      'TRAINER': 'info',
      'EMPLOYEE': 'primary'
    };
    return <Badge bg={variants[role] || 'secondary'}>{role}</Badge>;
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  const analytics = getAnalytics();

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-chart-bar me-2"></i>
            Reports & Analytics
          </h1>
          <p className="text-muted">Comprehensive training analytics and insights</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      {/* Report Navigation */}
      <Row className="mb-4">
        <Col>
          <div className="d-flex gap-2 flex-wrap">
            <Button 
              variant={selectedReport === 'overview' ? 'primary' : 'outline-primary'}
              onClick={() => setSelectedReport('overview')}
            >
              Overview
            </Button>
            <Button 
              variant={selectedReport === 'users' ? 'primary' : 'outline-primary'}
              onClick={() => setSelectedReport('users')}
            >
              User Analytics
            </Button>
            <Button 
              variant={selectedReport === 'courses' ? 'primary' : 'outline-primary'}
              onClick={() => setSelectedReport('courses')}
            >
              Course Analytics
            </Button>
            <Button 
              variant={selectedReport === 'departments' ? 'primary' : 'outline-primary'}
              onClick={() => setSelectedReport('departments')}
            >
              Department Reports
            </Button>
            <Button 
              variant={selectedReport === 'platforms' ? 'primary' : 'outline-primary'}
              onClick={() => setSelectedReport('platforms')}
            >
              Platform Analytics
            </Button>
          </div>
        </Col>
      </Row>

      {/* Overview Report */}
      {selectedReport === 'overview' && (
        <div>
          {/* Key Metrics */}
          <Row className="mb-4">
            <Col md={3}>
              <Card className="dashboard-card text-center">
                <Card.Body>
                  <i className="fas fa-users fa-2x text-primary mb-2"></i>
                  <h4>{analytics.totalUsers}</h4>
                  <p className="text-muted mb-0">Total Users</p>
                  <small className="text-success">{analytics.activeUsers} Active</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="dashboard-card text-center">
                <Card.Body>
                  <i className="fas fa-book fa-2x text-success mb-2"></i>
                  <h4>{analytics.totalCourses}</h4>
                  <p className="text-muted mb-0">Total Courses</p>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="dashboard-card text-center">
                <Card.Body>
                  <i className="fas fa-user-graduate fa-2x text-info mb-2"></i>
                  <h4>{analytics.totalEnrollments}</h4>
                  <p className="text-muted mb-0">Total Enrollments</p>
                  <small className="text-success">{analytics.completedEnrollments} Completed</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="dashboard-card text-center">
                <Card.Body>
                  <i className="fas fa-clock fa-2x text-warning mb-2"></i>
                  <h4>{formatTime(analytics.totalTimeSpent)}</h4>
                  <p className="text-muted mb-0">Total Time Spent</p>
                  <small className="text-info">{analytics.averageCompletion.toFixed(1)}% Avg Completion</small>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Recent Activity */}
          <Row>
            <Col md={6}>
              <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-chart-pie me-2"></i>
                    Enrollment Status
                  </h5>
                </Card.Header>
                <Card.Body>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <span>Completed</span>
                    <Badge bg="success">{analytics.completedEnrollments}</Badge>
                  </div>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <span>In Progress</span>
                    <Badge bg="warning">{analytics.inProgressEnrollments}</Badge>
                  </div>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <span>Enrolled</span>
                    <Badge bg="primary">{analytics.totalEnrollments - analytics.completedEnrollments - analytics.inProgressEnrollments}</Badge>
                  </div>
                </Card.Body>
              </Card>
            </Col>
            <Col md={6}>
              <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-users me-2"></i>
                    User Distribution
                  </h5>
                </Card.Header>
                <Card.Body>
                  {Object.entries(analytics.departmentStats).map(([dept, stats]) => (
                    <div key={dept} className="d-flex justify-content-between align-items-center mb-2">
                      <span>{dept}</span>
                      <Badge bg="info">{stats.count} users</Badge>
                    </div>
                  ))}
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>
      )}

      {/* User Analytics Report */}
      {selectedReport === 'users' && (
        <div>
          <Row>
            <Col>
              <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-users me-2"></i>
                    User Analytics
                  </h5>
                </Card.Header>
                <Card.Body>
                  <Table responsive striped>
                    <thead>
                      <tr>
                        <th>User</th>
                        <th>Role</th>
                        <th>Department</th>
                        <th>Enrollments</th>
                        <th>Completed</th>
                        <th>Time Spent</th>
                        <th>Avg Completion</th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.map((userData) => {
                        const userEnrollments = enrollments.filter(e => e.user.id === userData.id);
                        const userProgress = progress.filter(p => p.user.id === userData.id);
                        const completedCourses = userEnrollments.filter(e => e.status === 'COMPLETED').length;
                        const totalTimeSpent = userProgress.reduce((sum, p) => sum + (p.timeSpentMinutes || 0), 0);
                        const avgCompletion = userProgress.length > 0 ? 
                          userProgress.reduce((sum, p) => sum + (p.completionPercentage || 0), 0) / userProgress.length : 0;

                        return (
                          <tr key={userData.id}>
                            <td>
                              <strong>{userData.firstName} {userData.lastName}</strong>
                              <br />
                              <small className="text-muted">{userData.email}</small>
                            </td>
                            <td>{getRoleBadge(userData.role)}</td>
                            <td>{userData.department}</td>
                            <td>{userEnrollments.length}</td>
                            <td>
                              <Badge bg="success">{completedCourses}</Badge>
                            </td>
                            <td>{formatTime(totalTimeSpent)}</td>
                            <td>{avgCompletion.toFixed(1)}%</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>
      )}

      {/* Course Analytics Report */}
      {selectedReport === 'courses' && (
        <div>
          <Row>
            <Col>
              <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-book me-2"></i>
                    Course Analytics
                  </h5>
                </Card.Header>
                <Card.Body>
                  <Table responsive striped>
                    <thead>
                      <tr>
                        <th>Course</th>
                        <th>Category</th>
                        <th>Type</th>
                        <th>Enrollments</th>
                        <th>Completed</th>
                        <th>Avg Completion</th>
                        <th>Avg Time Spent</th>
                      </tr>
                    </thead>
                    <tbody>
                      {courses.map((course) => {
                        const courseEnrollments = enrollments.filter(e => e.course.id === course.id);
                        const courseProgress = progress.filter(p => p.course.id === course.id);
                        const completedEnrollments = courseEnrollments.filter(e => e.status === 'COMPLETED').length;
                        const avgCompletion = courseProgress.length > 0 ? 
                          courseProgress.reduce((sum, p) => sum + (p.completionPercentage || 0), 0) / courseProgress.length : 0;
                        const avgTimeSpent = courseProgress.length > 0 ? 
                          courseProgress.reduce((sum, p) => sum + (p.timeSpentMinutes || 0), 0) / courseProgress.length : 0;

                        return (
                          <tr key={course.id}>
                            <td>
                              <strong>{course.title}</strong>
                              <br />
                              <small className="text-muted">{course.description}</small>
                            </td>
                            <td>
                              <Badge bg="secondary">{course.category}</Badge>
                            </td>
                            <td>
                              <Badge bg="info">{course.type}</Badge>
                            </td>
                            <td>{courseEnrollments.length}</td>
                            <td>
                              <Badge bg="success">{completedEnrollments}</Badge>
                            </td>
                            <td>{avgCompletion.toFixed(1)}%</td>
                            <td>{formatTime(avgTimeSpent)}</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>
      )}

      {/* Department Reports */}
      {selectedReport === 'departments' && (
        <div>
          <Row>
            <Col>
              <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-building me-2"></i>
                    Department Analytics
                  </h5>
                </Card.Header>
                <Card.Body>
                  <Table responsive striped>
                    <thead>
                      <tr>
                        <th>Department</th>
                        <th>Users</th>
                        <th>Enrollments</th>
                        <th>Completed</th>
                        <th>Completion Rate</th>
                        <th>Avg Time Spent</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.entries(analytics.departmentStats).map(([dept, stats]) => {
                        const completionRate = stats.enrollments > 0 ? 
                          ((stats.completed / stats.enrollments) * 100).toFixed(1) : 0;

                        return (
                          <tr key={dept}>
                            <td>
                              <strong>{dept}</strong>
                            </td>
                            <td>{stats.count}</td>
                            <td>{stats.enrollments}</td>
                            <td>
                              <Badge bg="success">{stats.completed}</Badge>
                            </td>
                            <td>{completionRate}%</td>
                            <td>--</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </div>
      )}

      {/* Platform Analytics */}
      {selectedReport === 'platforms' && (
        <div>
          <Row>
        <Col>
          <Card className="dashboard-card">
                <Card.Header>
                  <h5 className="mb-0">
                    <i className="fas fa-globe me-2"></i>
                    Platform Analytics
                  </h5>
                </Card.Header>
                <Card.Body>
                  <Table responsive striped>
                    <thead>
                      <tr>
                        <th>Platform</th>
                        <th>Enrollments</th>
                        <th>Percentage</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.entries(analytics.platformStats).map(([platform, count]) => {
                        const percentage = ((count / analytics.totalEnrollments) * 100).toFixed(1);

                        return (
                          <tr key={platform}>
                            <td>
                              <strong>{platform}</strong>
                            </td>
                            <td>{count}</td>
                            <td>{percentage}%</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
            </Card.Body>
          </Card>
        </Col>
      </Row>
        </div>
      )}
    </Container>
  );
};

export default Reports;
