import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Alert } from 'react-bootstrap';
import apiClient from '../services/apiClient';

const Enrollments = ({ user }) => {
  const [enrollments, setEnrollments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadEnrollments();
  }, []);

  const loadEnrollments = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get(`/enrollments/user/${user.id}`);
      let apiEnrollments = response.data;

      // Get enrollments from localStorage (new enrollments from Courses section)
      const localEnrollments = JSON.parse(localStorage.getItem('enrollments') || '[]');
      const userLocalEnrollments = localEnrollments.filter(e => e.userId === user.id);

      // Combine API and local enrollments
      const allEnrollments = [...userLocalEnrollments, ...apiEnrollments];

      // Sort enrollments: ENROLLED (newest first) -> IN_PROGRESS -> COMPLETED
      const sortedEnrollments = allEnrollments.sort((a, b) => {
        // First sort by status priority
        const statusPriority = { 'ENROLLED': 0, 'IN_PROGRESS': 1, 'COMPLETED': 2 };
        const statusA = statusPriority[a.status] || 3;
        const statusB = statusPriority[b.status] || 3;

        if (statusA !== statusB) {
          return statusA - statusB;
        }

        // Then sort by date (newest first)
        const dateA = new Date(a.enrolledAt || a.createdAt || 0);
        const dateB = new Date(b.enrolledAt || b.createdAt || 0);
        return dateB - dateA;
      });

      setEnrollments(sortedEnrollments);
    } catch (err) {
      setError('Failed to load enrollments');
      console.error(err);
      // Set default enrollments if API fails
      const defaultEnrollments = [
        {
          id: 1,
          status: 'COMPLETED',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 45 * 24 * 60 * 60 * 1000),
          completedAt: new Date(Date.now() - 5 * 24 * 60 * 60 * 1000),
          completionPercentage: 100.0,
          grade: 'A+',
          certificateEarned: true,
          notes: 'Completed successfully',
          course: {
            id: 1,
            title: 'Java Programming Fundamentals',
            description: 'Learn core Java concepts and object-oriented programming',
            category: 'TECHNICAL',
            type: 'VIRTUAL',
            durationHours: 40,
            materials: 'https://www.udemy.com/course/java-programming-tutorial-for-beginners/',
            prerequisites: 'Basic computer knowledge',
            isMandatory: true,
            maxParticipants: 50,
            difficultyLevel: 'BEGINNER',
            completionRate: 85,
            averageRating: 4.8,
            reviewCount: 1250
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 2,
          status: 'COMPLETED',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
          completedAt: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
          completionPercentage: 100.0,
          grade: 'A',
          certificateEarned: true,
          notes: 'Required for role',
          course: {
            id: 2,
            title: 'Spring Boot Development',
            description: 'Master Spring Boot framework for building web applications',
            category: 'TECHNICAL',
            type: 'HYBRID',
            durationHours: 60,
            materials: 'https://www.udemy.com/course/spring-boot-tutorial-for-beginners/',
            prerequisites: 'Java Programming Fundamentals',
            isMandatory: true,
            maxParticipants: 30,
            difficultyLevel: 'INTERMEDIATE',
            completionRate: 78,
            averageRating: 4.6,
            reviewCount: 890
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 3,
          status: 'IN_PROGRESS',
          type: 'OPTIONAL',
          enrolledAt: new Date(Date.now() - 15 * 24 * 60 * 60 * 1000),
          completedAt: null,
          completionPercentage: 65.0,
          grade: null,
          certificateEarned: false,
          notes: 'Career development',
          course: {
            id: 3,
            title: 'React.js for Beginners',
            description: 'Learn React.js for building modern web applications',
            category: 'TECHNICAL',
            type: 'SELF_PACED',
            durationHours: 35,
            materials: 'https://www.udemy.com/course/react-the-complete-guide-incl-redux/',
            prerequisites: 'JavaScript basics',
            isMandatory: false,
            maxParticipants: 40,
            difficultyLevel: 'BEGINNER',
            completionRate: 82,
            averageRating: 4.7,
            reviewCount: 2100
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        },
        {
          id: 4,
          status: 'IN_PROGRESS',
          type: 'MANDATORY',
          enrolledAt: new Date(Date.now() - 10 * 24 * 60 * 60 * 1000),
          completedAt: null,
          completionPercentage: 45.0,
          grade: null,
          certificateEarned: false,
          notes: 'Compliance requirement',
          course: {
            id: 4,
            title: 'Leadership Skills',
            description: 'Develop essential leadership and management skills',
            category: 'LEADERSHIP',
            type: 'IN_PERSON',
            durationHours: 20,
            materials: 'Internal materials',
            prerequisites: 'None',
            isMandatory: false,
            maxParticipants: 25,
            difficultyLevel: 'INTERMEDIATE',
            completionRate: 92,
            averageRating: 4.5,
            reviewCount: 156
          },
          user: {
            id: 4,
            firstName: 'Employee',
            lastName: 'User',
            email: 'employee@sasken.com',
            department: 'Engineering',
            role: 'EMPLOYEE'
          }
        }
      ];

      // Get local enrollments and combine with defaults
      const localEnrollments = JSON.parse(localStorage.getItem('enrollments') || '[]');
      const userLocalEnrollments = localEnrollments.filter(e => e.userId === user.id);
      const allEnrollments = [...userLocalEnrollments, ...defaultEnrollments];

      // Sort enrollments: ENROLLED (newest first) -> IN_PROGRESS -> COMPLETED
      const sortedEnrollments = allEnrollments.sort((a, b) => {
        // First sort by status priority
        const statusPriority = { 'ENROLLED': 0, 'IN_PROGRESS': 1, 'COMPLETED': 2 };
        const statusA = statusPriority[a.status] || 3;
        const statusB = statusPriority[b.status] || 3;

        if (statusA !== statusB) {
          return statusA - statusB;
        }

        // Then sort by date (newest first)
        const dateA = new Date(a.enrolledAt || a.createdAt || 0);
        const dateB = new Date(b.enrolledAt || b.createdAt || 0);
        return dateB - dateA;
      });

      setEnrollments(sortedEnrollments);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      'ENROLLED': 'primary',
      'IN_PROGRESS': 'warning',
      'COMPLETED': 'success',
      'DROPPED': 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status.replace('_', ' ')}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      'MANDATORY': 'danger',
      'OPTIONAL': 'info',
      'RECOMMENDED': 'warning'
    };
    return <Badge bg={variants[type] || 'secondary'}>{type}</Badge>;
  };

  const getDifficultyBadge = (difficulty) => {
    const variants = {
      'BEGINNER': 'success',
      'INTERMEDIATE': 'warning',
      'ADVANCED': 'danger'
    };
    return <Badge bg={variants[difficulty] || 'secondary'}>{difficulty}</Badge>;
  };

  const getPlatformBadge = (materials) => {
    if (!materials) return <Badge bg="secondary">Internal</Badge>;

    if (materials.includes('udemy.com')) {
      return <Badge bg="purple">Udemy</Badge>;
    } else if (materials.includes('hackerrank.com')) {
      return <Badge bg="orange">HackerRank</Badge>;
    } else if (materials.includes('intershala.com')) {
      return <Badge bg="blue">Internshala</Badge>;
    } else if (materials.includes('coursera.org')) {
      return <Badge bg="teal">Coursera</Badge>;
    } else if (materials.includes('edx.org')) {
      return <Badge bg="dark">edX</Badge>;
    } else if (materials.includes('http')) {
      return <Badge bg="info">External</Badge>;
    }
    return <Badge bg="secondary">Internal</Badge>;
  };

  const formatDate = (date) => {
    if (!date) return 'N/A';
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-user-graduate me-2"></i>
            Enrollment History
          </h1>
          <p className="text-muted">Track your course enrollments and progress</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                My Enrollments ({enrollments.length})
              </h5>
            </Card.Header>
            <Card.Body>
              {enrollments.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-user-graduate fa-3x text-muted mb-3"></i>
                  <h5>No Enrollments Yet</h5>
                  <p className="text-muted">Enroll in courses from the Courses section to see them here.</p>
                </div>
              ) : (
                <div>
                  {enrollments.map((enrollment) => (
                    <div key={enrollment.id} className="mb-4 p-4 border rounded shadow-sm">
                      <Row className="align-items-center mb-3">
                        <Col md={6}>
                          <h5 className="mb-2">{enrollment.course?.title}</h5>
                          <p className="text-muted mb-2">{enrollment.course?.description}</p>
                          <div className="d-flex align-items-center gap-2 mb-2">
                            <Badge bg="secondary">{enrollment.course?.category}</Badge>
                            <Badge bg="info">{enrollment.course?.type}</Badge>
                            <small className="text-muted">
                              <i className="fas fa-clock me-1"></i>
                              {enrollment.course?.durationHours} hours
                            </small>
                          </div>
                          <small className="text-muted">
                            Enrolled: {formatDate(enrollment.enrolledAt)}
                            {enrollment.completedAt && (
                              <span className="ms-3">
                                Completed: {formatDate(enrollment.completedAt)}
                              </span>
                            )}
                          </small>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            {getStatusBadge(enrollment.status)}
                            <br />
                            <small className="text-muted">Status</small>
                          </div>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            <div className="h5 mb-1 text-primary">
                              {enrollment.completionPercentage?.toFixed(1) || 0}%
                            </div>
                            <small className="text-muted">Progress</small>
                          </div>
                        </Col>
                        <Col md={2}>
                          <div className="text-center">
                            {enrollment.grade ? (
                              <Badge bg="success">{enrollment.grade}</Badge>
                            ) : (
                              <small className="text-muted">No grade yet</small>
                            )}
                            <br />
                            <small className="text-muted">Grade</small>
                          </div>
                        </Col>
                      </Row>

                      {/* Course Details */}
                      <Row className="mb-3">
                        <Col>
                          <div className="bg-light p-3 rounded">
                            <Row>
                              <Col md={3}>
                                <small className="text-muted">
                                  <strong>Difficulty:</strong><br />
                                  {getDifficultyBadge(enrollment.course?.difficultyLevel)}
                                </small>
                              </Col>
                              <Col md={3}>
                                <small className="text-muted">
                                  <strong>Completion Rate:</strong><br />
                                  {enrollment.course?.completionRate}%
                                </small>
                              </Col>
                              <Col md={3}>
                                <small className="text-muted">
                                  <strong>Rating:</strong><br />
                                   {enrollment.course?.averageRating} ({enrollment.course?.reviewCount} reviews)
                                </small>
                              </Col>
                              <Col md={3}>
                                <small className="text-muted">
                                  <strong>Platform:</strong><br />
                                  {getPlatformBadge(enrollment.course?.materials)}
                                </small>
                              </Col>
                            </Row>
                          </div>
                        </Col>
                      </Row>

                      {/* Progress Bar */}
                      <Row className="mb-3">
                        <Col>
                          <div className="d-flex justify-content-between align-items-center mb-2">
                            <small className="text-muted">Progress</small>
                            <div className="d-flex align-items-center gap-2">
                              {getTypeBadge(enrollment.type)}
                              {enrollment.certificateEarned && (
                                <Badge bg="success">
                                  <i className="fas fa-certificate me-1"></i>
                                  Certificate
                                </Badge>
                              )}
                            </div>
                          </div>
                          <div className="progress" style={{ height: '12px' }}>
                            <div
                              className="progress-bar"
                              style={{ width: `${enrollment.completionPercentage || 0}%` }}
                            ></div>
                          </div>
                        </Col>
                      </Row>

                      {/* Actions */}
                      <Row>
                        <Col>
                          <div className="d-flex gap-2">
                            <Button 
                              size="sm" 
                              variant="outline-primary"
                              title="Continue Learning"
                            >
                              <i className="fas fa-play me-1"></i>
                              Continue
                            </Button>
                            {enrollment.course?.materials && enrollment.course.materials.startsWith('http') && (
                              <Button 
                                size="sm" 
                                variant="outline-info"
                                title="Go to Course"
                                onClick={() => window.open(enrollment.course.materials, '_blank')}
                              >
                                <i className="fas fa-external-link-alt me-1"></i>
                                Go to Course
                              </Button>
                            )}
                            <Button 
                              size="sm" 
                              variant="outline-secondary"
                              title="View Details"
                            >
                              <i className="fas fa-eye me-1"></i>
                              Details
                            </Button>
                          </div>
                        </Col>
                      </Row>
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Enrollment Statistics */}
      <Row className="mt-4">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-chart-pie me-2"></i>
                Enrollment Statistics
              </h5>
            </Card.Header>
            <Card.Body>
              <Row>
                <Col md={3} className="text-center">
                  <h4 className="text-primary">{enrollments.length}</h4>
                  <p className="text-muted mb-0">Total Enrollments</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-success">{enrollments.filter(e => e.status === 'COMPLETED').length}</h4>
                  <p className="text-muted mb-0">Completed</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-warning">{enrollments.filter(e => e.status === 'IN_PROGRESS').length}</h4>
                  <p className="text-muted mb-0">In Progress</p>
                </Col>
                <Col md={3} className="text-center">
                  <h4 className="text-info">{enrollments.filter(e => e.status === 'ENROLLED').length}</h4>
                  <p className="text-muted mb-0">Newly Enrolled</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Enrollments;
