import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ProgressBar, Badge, Button, Alert } from 'react-bootstrap';
import { ProgressService } from '../services/ProgressService';
import { EnrollmentService } from '../services/EnrollmentService';

const Progress = ({ user }) => {
  const [progressData, setProgressData] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadProgressData();
    loadEnrollments();
    loadStats();
  }, []);

  const loadProgressData = async () => {
    try {
      setLoading(true);
      const data = await ProgressService.getProgressByUser(user.id);
      setProgressData(data);
    } catch (err) {
      setError('Failed to load progress data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadEnrollments = async () => {
    try {
      const data = await EnrollmentService.getEnrollmentsByUser(user.id);
      setEnrollments(data);
    } catch (err) {
      console.error('Failed to load enrollments:', err);
    }
  };

  const loadStats = async () => {
    try {
      const data = await ProgressService.getUserProgressStats(user.id);
      setStats(data);
    } catch (err) {
      console.error('Failed to load stats:', err);
    }
  };

  const getProgressVariant = (percentage) => {
    if (percentage >= 80) return 'success';
    if (percentage >= 60) return 'warning';
    if (percentage >= 40) return 'info';
    return 'danger';
  };

  const formatTime = (minutes) => {
    if (!minutes) return '0h 0m';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getGradeBadge = (grade) => {
    if (!grade) return null;
    const variants = {
      'A': 'success',
      'B': 'info',
      'C': 'warning',
      'D': 'danger',
      'F': 'danger'
    };
    return <Badge bg={variants[grade] || 'secondary'}>{grade}</Badge>;
  };

  if (loading) {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col md={6} className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1 className="fw-bold text-primary">
            <i className="fas fa-chart-line me-2"></i>
            Learning Progress
          </h1>
          <p className="text-muted">Track your learning progress and achievements</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}

      {/* Progress Stats */}
      {stats && (
        <Row className="mb-4">
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-book fa-2x text-primary mb-2"></i>
                <h4>{stats.totalCourses}</h4>
                <p className="text-muted mb-0">Total Courses</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-check-circle fa-2x text-success mb-2"></i>
                <h4>{stats.completedCourses}</h4>
                <p className="text-muted mb-0">Completed</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-percentage fa-2x text-info mb-2"></i>
                <h4>{stats.averageCompletion.toFixed(1)}%</h4>
                <p className="text-muted mb-0">Avg Completion</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card className="dashboard-card text-center">
              <Card.Body>
                <i className="fas fa-clock fa-2x text-warning mb-2"></i>
                <h4>{formatTime(stats.totalTimeSpent)}</h4>
                <p className="text-muted mb-0">Total Time</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      {/* Course Progress */}
      <Row>
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Course Progress
              </h5>
            </Card.Header>
            <Card.Body>
              {progressData.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-chart-line fa-3x text-muted mb-3"></i>
                  <h5>No Progress Data</h5>
                  <p className="text-muted">Start learning to see your progress here.</p>
                </div>
              ) : (
                <div>
                  {progressData.map((progress) => (
                    <div key={progress.id} className="mb-4 p-3 border rounded">
                      <Row className="align-items-center">
                        <Col md={4}>
                          <h6 className="mb-1">{progress.course?.title}</h6>
                          <small className="text-muted">{progress.course?.category}</small>
                        </Col>
                        <Col md={3}>
                          <div className="mb-2">
                            <small className="text-muted">Completion</small>
                            <div className="d-flex justify-content-between">
                              <span>{progress.completionPercentage?.toFixed(1)}%</span>
                              <span>{getGradeBadge(progress.grade)}</span>
                            </div>
                          </div>
                          <ProgressBar 
                            now={progress.completionPercentage || 0} 
                            variant={getProgressVariant(progress.completionPercentage || 0)}
                            className="mb-1"
                          />
                        </Col>
                        <Col md={2}>
                          <small className="text-muted">Time Spent</small>
                          <div>{formatTime(progress.timeSpentMinutes)}</div>
                        </Col>
                        <Col md={2}>
                          <small className="text-muted">Last Accessed</small>
                          <div>
                            {progress.lastAccessedAt ? 
                              new Date(progress.lastAccessedAt).toLocaleDateString() : 
                              'N/A'
                            }
                          </div>
                        </Col>
                        <Col md={1}>
                          <Button 
                            size="sm" 
                            variant="outline-primary"
                            title="Continue Learning"
                          >
                            <i className="fas fa-play"></i>
                          </Button>
                        </Col>
                      </Row>
                      {progress.quizScore && (
                        <Row className="mt-2">
                          <Col md={4} className="offset-md-4">
                            <small className="text-muted">Quiz Score</small>
                            <div>
                              {progress.quizScore}/{progress.maxQuizScore} 
                              ({((progress.quizScore / progress.maxQuizScore) * 100).toFixed(1)}%)
                            </div>
                          </Col>
                        </Row>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Recent Enrollments */}
      <Row className="mt-4">
        <Col>
          <Card className="dashboard-card">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-clock me-2"></i>
                Recent Enrollments
              </h5>
            </Card.Header>
            <Card.Body>
              {enrollments.length === 0 ? (
                <p className="text-muted text-center">No recent enrollments</p>
              ) : (
                <div>
                  {enrollments.slice(0, 5).map((enrollment) => (
                    <div key={enrollment.id} className="d-flex justify-content-between align-items-center py-2 border-bottom">
                      <div>
                        <strong>{enrollment.course?.title}</strong>
                        <br />
                        <small className="text-muted">
                          Enrolled: {enrollment.enrolledAt ? 
                            new Date(enrollment.enrolledAt).toLocaleDateString() : 
                            'N/A'
                          }
                        </small>
                      </div>
                      <Badge bg={enrollment.status === 'COMPLETED' ? 'success' : 'primary'}>
                        {enrollment.status}
                      </Badge>
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Progress;
